    private void plusButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_plusButtonActionPerformed
        this.firstNumber = Float.parseFloat(this.visor.getText());
        this.operator = "+";
        this.visor.setText("");
    }//GEN-LAST:event_plusButtonActionPerformed

    private void equalButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_equalButtonActionPerformed
        this.secondNumber = Float.parseFloat(this.visor.getText());

        switch (this.operator) {
            case "+":
                this.visor.setText(withoutPoint(this.firstNumber + this.secondNumber));
                break;
            case "-":
                this.visor.setText(withoutPoint(this.firstNumber - this.secondNumber));
                break;
            case "*":
                this.visor.setText(withoutPoint(this.firstNumber * this.secondNumber));
                break;
            case "/":
                if (this.secondNumber == 0) {
                    this.visor.setText("Can't divide by zero");
                } else {
                    this.visor.setText(withoutPoint(this.firstNumber / this.secondNumber));
                }
                break;
        }
    }//GEN-LAST:event_equalButtonActionPerformed

