    private void pointButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pointButtonActionPerformed
        if (!(this.visor.getText().contains("."))) {
            this.visor.setText(this.visor.getText() + ".");

        }
    }//GEN-LAST:event_pointButtonActionPerformed

    public String withoutPoint(float result) {
        String answer = "";

        answer = Float.toString(result);

        if (result % 1 == 0) {
            answer = answer.substring(0, answer.length() - 2);

        }

        return answer;
    }
        
    
